mapper.select:
SELECT 
FROM u_product
WHERE is_deleted = 0

    ORDER BY ${paging.orderByClause}

LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE u_product
SET is_deleted = 1
WHERE u_product_id = #{id}

mapper.select:
SELECT EXISTS(
    SELECT 1
    FROM u_product
    WHERE u_product_id = #{id}
      AND is_deleted = 0
)
