mapper.select:
SELECT u.user_id, u.username, u.phone_number, u.password, u.ho_va_ten, u.ma_so_thue, u.bank_name, u.bank_number, u.description, u.role_id, u.is_active, u.created_at, u.created_by, u.updated_at, u.updated_by, u.is_deleted, r.role_name, l.u_location_id, l.address01, l.address02, l.address03, l.address04, l.address05, l.full_address FROM sys_user u LEFT JOIN sys_role r ON u.role_id = r.id LEFT JOIN u_location l ON u.user_id = l.user_id WHERE u.is_deleted = 0
ORDER BY ${paging.orderByClause}
LIMIT #{limit} OFFSET #{offset}

mapper.delete:
UPDATE sys_user
SET is_deleted = 1
WHERE user_id = #{id}
