mapper.select:
SELECT master_menu_id, master_menu_name, path, icon, is_parent, order_index, parent_id, status, noted, created_at, created_by, updated_at, updated_by, is_deleted FROM master_menu WHERE is_deleted = 0
ORDER BY ${paging.orderByClause}
LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE master_menu


        , master_menu_name = #{masterMenuName}


        , path = #{path}


        , icon = #{icon}


        , is_parent = #{isParent}


        , order_index = #{orderIndex}


        , parent_id = #{parentId}


        , status = #{status}


        , noted = #{note}

    , updated_by = #{updatedBy}

        , updated_at = #{updatedAt}



WHERE master_menu_id = #{masterMenuId} AND is_deleted = 0

UPDATE master_menu
SET is_deleted = 1
WHERE master_menu_id = #{id}
