mapper.select:
WITH user_data AS ( SELECT u.user_id, u.username, u.password, u.phone_number, u.ho_va_ten, u.ma_so_thue, u.master_location_code, u.bank_name, u.bank_number, u.description AS user_description, u.role_id, u.is_active, u.old_id, u.status, u.noted, u.created_at, u.created_by, u.updated_at, u.updated_by, u.is_deleted, r.role_name, r.description AS role_description, l.u_location_id AS locationId, l.latitude, l.longitude, l.address01, l.address02, l.address03, l.address04, l.address05, l.full_address AS fullAddress FROM sys_user u LEFT JOIN sys_role r ON u.role_id = r.id LEFT JOIN u_location l ON u.user_id = l.user_id WHERE u.is_deleted = 0
AND (
u.ho_va_ten LIKE CONCAT('%', #{f}, '%') OR u.username LIKE CONCAT('%', #{f}, '%') OR u.phone_number LIKE CONCAT('%', #{f}, '%') OR r.role_name LIKE CONCAT('%', #{f}, '%') OR l.full_address LIKE CONCAT('%', #{f}, '%')
)
) SELECT * FROM user_data ORDER BY created_at DESC LIMIT #{limit} OFFSET #{offset}

WITH user_data AS ( SELECT u.user_id FROM sys_user u LEFT JOIN sys_role r ON u.role_id = r.id LEFT JOIN u_location l ON u.user_id = l.user_id WHERE u.is_deleted = 0
AND (
u.ho_va_ten LIKE CONCAT('%', #{f}, '%') OR u.username LIKE CONCAT('%', #{f}, '%') OR u.phone_number LIKE CONCAT('%', #{f}, '%') OR r.role_name LIKE CONCAT('%', #{f}, '%') OR l.full_address LIKE CONCAT('%', #{f}, '%')
)
) SELECT COUNT(*) FROM user_data

mapper.update:
UPDATE sys_user SET username = #{username},
password = #{password},
ho_va_ten = #{hoVaTen}, phone_number = #{phoneNumber}, ma_so_thue = #{maSoThue}, master_location_code = #{masterLocationCode}, bank_name = #{bankName}, bank_number = #{bankNumber}, description = #{description}, role_id = #{roleId}, is_active = #{isActive}, old_id = #{oldId}, status = #{status}, noted = #{note}, updated_at = NOW(), updated_by = #{updatedBy} WHERE user_id = #{userId}

UPDATE sys_user
SET is_deleted = 1,
    updated_at = NOW(),
    updated_by = #{updatedBy}
WHERE user_id = #{id}

mapper.select:
SELECT user_id
FROM sys_user
WHERE phone_number = #{phoneNumber}

SELECT user_id
FROM sys_user
WHERE phone_number = #{phoneNumber}
  AND user_id != #{userId}
